// src/components/Body.js
import React, { useState, useEffect } from 'react';
import './Body.css';

const Body = () => {
  const [tableData, setTableData] = useState([]); // Original Data
  const [filteredData, setFilteredData] = useState([]); // Data after Filtering
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);

  // Search States
  const [searchName, setSearchName] = useState('');
  const [searchAge, setSearchAge] = useState('');
  const [searchEmail, setSearchEmail] = useState('');

  // Fetch data from FastAPI on component mount
  useEffect(() => {
    fetch('http://localhost:8000/api/data')  // FastAPI endpoint
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        setTableData(data.data);      // Store original data
        setFilteredData(data.data);   // Initialize filtered data
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
        setError(true);
        setLoading(false);
      });
  }, []);

  // Handle Search Filtering
  useEffect(() => {
    let filtered = tableData;

    if (searchName.trim() !== '') {
      filtered = filtered.filter((item) =>
        item.name.toLowerCase().includes(searchName.toLowerCase())
      );
    }

    if (searchAge.trim() !== '') {
      filtered = filtered.filter((item) =>
        item.age.toString().includes(searchAge)
      );
    }

    if (searchEmail.trim() !== '') {
      filtered = filtered.filter((item) =>
        item.email.toLowerCase().includes(searchEmail.toLowerCase())
      );
    }

    setFilteredData(filtered);
  }, [searchName, searchAge, searchEmail, tableData]);

  // Handle Reset Search
  const handleReset = () => {
    setSearchName('');
    setSearchAge('');
    setSearchEmail('');
  };

  if (loading) {
    return (
      <div className="body-container">
        <h2>Loading data...</h2>
      </div>
    );
  }

  if (error) {
    return (
      <div className="body-container">
        <h2>Error loading data.</h2>
      </div>
    );
  }

  return (
    <div className="body-container">
      <h2>Data from CSV</h2>

      {/* Search Section */}
      <div className="search-section">
        <input
          type="text"
          placeholder="Search by Name"
          value={searchName}
          onChange={(e) => setSearchName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Search by Age"
          value={searchAge}
          onChange={(e) => setSearchAge(e.target.value)}
        />
        <input
          type="text"
          placeholder="Search by Email"
          value={searchEmail}
          onChange={(e) => setSearchEmail(e.target.value)}
        />
        <button onClick={handleReset} className="reset-button">Reset</button>
      </div>

      {/* Table Section */}
      <table className="data-table">
        <thead>
          <tr>
            {/* Dynamically create table headers based on keys */}
            {filteredData.length > 0 &&
              Object.keys(filteredData[0]).map((key) => (
                <th key={key}>{key.toUpperCase()}</th>
              ))}
          </tr>
        </thead>
        <tbody>
          {filteredData.length === 0 ? (
            <tr>
              <td colSpan="100%">No data available.</td>
            </tr>
          ) : (
            filteredData.map((row, index) => (
              <tr key={index}>
                {Object.values(row).map((value, idx) => (
                  <td key={idx}>{value}</td>
                ))}
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Body;
