// src/components/Body.js
import React, { useState, useEffect } from 'react';
import './Body.css';
import Pagination from './Pagination';

const Body = () => {
  const [tableData, setTableData] = useState([]); // Original Data
  const [filteredData, setFilteredData] = useState([]); // Data after Filtering
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);

  // Search States
  const [searchCountry, setSearchCountry] = useState('');
  const [searchYear, setSearchYear] = useState('');
  const [searchPollutant, setSearchPollutant] = useState('');
  const [searchExposureMean, setSearchExposureMean] = useState('');
  const [searchUnits, setSearchUnits] = useState('');
  const [searchBurdenMean, setSearchBurdenMean] = useState('');
  const [searchMeasure, setSearchMeasure] = useState('');
  const [searchMetric, setSearchMetric] = useState('');
  const [searchCauseName, setSearchCauseName] = useState('');

  const [uniqueYears, setUniqueYears] = useState([]);
  const [uniquePollutants, setUniquePollutants] = useState([]);
  const [uniqueUnits, setUniqueUnits] = useState([]);
  const [uniqueMeasures, setUniqueMeasures] = useState([]);
  const [uniqueMetrics, setUniqueMetrics] = useState([]);
  const [uniqueCauseNames, setUniqueCauseNames] = useState([]);

  // Pagination States
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(10); // Default to 10

  // Fetch data from FastAPI on component mount
useEffect(() => {
  fetch('http://localhost:8000/api/data') // Replace with your FastAPI endpoint
    .then((response) => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      return response.json();
    })
    .then((data) => {
      // Process data to rename 'UNNAMED: 0' to 'index'
      const processedData = data.data.map((item, idx) => {
        const newItem = { ...item };
          newItem.index = idx + 1; // Fallback to sequential index
        return newItem;
      });

      setTableData(processedData);      // Store original data
      setFilteredData(processedData);   // Initialize filtered data

      // Extract unique years
      const years = Array.from(new Set(processedData.map(item => item.Year))).sort((a, b) => a - b);
      setUniqueYears(years);

      // Extract unique values for other fields
      const pollutants = Array.from(new Set(processedData.map(item => item.Pollutant))).sort();
      setUniquePollutants(pollutants);

      const units = Array.from(new Set(processedData.map(item => item.Units))).sort();
      setUniqueUnits(units);

      const measures = Array.from(new Set(processedData.map(item => item.Measure))).sort();
      setUniqueMeasures(measures);

      const metrics = Array.from(new Set(processedData.map(item => item.Metric))).sort();
      setUniqueMetrics(metrics);

      const causeNames = Array.from(new Set(processedData.map(item => item['Cause Name']))).sort();
      setUniqueCauseNames(causeNames);

      setLoading(false);
    })
    .catch((error) => {
      console.error('Error fetching data:', error);
      setError(true);
      setLoading(false);
    });
}, []);



  // Handle Search Filtering
  useEffect(() => {
    let filtered = tableData;

    if (searchCountry.trim() !== '') {
      filtered = filtered.filter((item) =>
        item.Country.toLowerCase().includes(searchCountry.toLowerCase())
      );
    }

    if (searchYear.trim() !== '') {
      filtered = filtered.filter((item) =>
        item.Year.toString().includes(searchYear)
      );
    }

    if (searchPollutant.trim() !== '') {
      filtered = filtered.filter((item) =>
        item.Pollutant.toLowerCase().includes(searchPollutant.toLowerCase())
      );
    }

    if (searchExposureMean.trim() !== '') {
      filtered = filtered.filter((item) =>
        item['Exposure Mean'].toString().includes(searchExposureMean)
      );
    }

    if (searchUnits.trim() !== '') {
      filtered = filtered.filter((item) =>
        item.Units.toLowerCase().includes(searchUnits.toLowerCase())
      );
    }

    if (searchBurdenMean.trim() !== '') {
      filtered = filtered.filter((item) =>
        item['Burden Mean'].toString().includes(searchBurdenMean)
      );
    }

    if (searchMeasure.trim() !== '') {
      filtered = filtered.filter((item) =>
        item.Measure.toLowerCase().includes(searchMeasure.toLowerCase())
      );
    }

    if (searchMetric.trim() !== '') {
      filtered = filtered.filter((item) =>
        item.Metric.toLowerCase().includes(searchMetric.toLowerCase())
      );
    }

    if (searchCauseName.trim() !== '') {
      filtered = filtered.filter((item) =>
        item['Cause Name'].toLowerCase().includes(searchCauseName.toLowerCase())
      );
    }

    setFilteredData(filtered);
    setCurrentPage(1); // Reset to first page on search
  }, [
    searchCountry,
    searchYear,
    searchPollutant,
    searchExposureMean,
    searchUnits,
    searchBurdenMean,
    searchMeasure,
    searchMetric,
    searchCauseName,
    tableData,
  ]);

  // Calculate total pages
  const totalPages =
    itemsPerPage === 'all'
      ? 1
      : Math.ceil(filteredData.length / itemsPerPage);

  // Calculate current items to display
  const indexOfLastItem =
    itemsPerPage === 'all' ? filteredData.length : currentPage * itemsPerPage;
  const indexOfFirstItem =
    itemsPerPage === 'all' ? 0 : indexOfLastItem - itemsPerPage;
  const currentItems =
    itemsPerPage === 'all'
      ? filteredData
      : filteredData.slice(indexOfFirstItem, indexOfLastItem);

  // Handle Page Change
  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  // Handle Reset Search
  const handleReset = () => {
    setSearchCountry('');
    setSearchYear('');
    setSearchPollutant('');
    setSearchExposureMean('');
    setSearchUnits('');
    setSearchBurdenMean('');
    setSearchMeasure('');
    setSearchMetric('');
    setSearchCauseName('');
    setCurrentPage(1);
    setItemsPerPage(10);
  };

  if (loading) {
    return (
      <div className="body-container">
        <h2>Loading data...</h2>
      </div>
    );
  }

  if (error) {
    return (
      <div className="body-container">
        <h2>Error loading data.</h2>
      </div>
    );
  }

  return (
    <div className="body-container">
      <h2>Data from CSV</h2>

      {/* Search Section */}
      <div className="search-group">
        <input
            type="text"
            placeholder="Search by Country"
            value={searchCountry}
            onChange={(e) => setSearchCountry(e.target.value)}
        />
        {/* Year Dropdown */}
        <select
            id="searchYear"
            value={searchYear}
            onChange={(e) => setSearchYear(e.target.value)}
        >
          <option value="">All Years</option>
          {uniqueYears.map((year) => (
              <option key={year} value={year}>
                {year}
              </option>
          ))}
        </select>
        {/* Pollutant Dropdown */}
        <select
            id="searchPollutant"
            value={searchPollutant}
            onChange={(e) => setSearchPollutant(e.target.value)}
        >
          <option value="">All Pollutants</option>
          {uniquePollutants.map((pollutant) => (
              <option key={pollutant} value={pollutant}>
                {pollutant}
              </option>
          ))}
        </select>
      </div>
      <div className="search-group">
        {/* Exposure Mean Input */}
        <input
            type="text"
            placeholder="Search by Exposure Mean"
            value={searchExposureMean}
            onChange={(e) => setSearchExposureMean(e.target.value)}
        />
        {/* Units Dropdown */}
        <select
            id="searchUnits"
            value={searchUnits}
            onChange={(e) => setSearchUnits(e.target.value)}
        >
          <option value="">All Units</option>
          {uniqueUnits.map((unit) => (
              <option key={unit} value={unit}>
                {unit}
              </option>
          ))}
        </select>
        {/* Burden Mean Input */}
        <input
            type="text"
            placeholder="Search by Burden Mean"
            value={searchBurdenMean}
            onChange={(e) => setSearchBurdenMean(e.target.value)}
        />
      </div>
      <div className="search-group">
        {/* Measure Dropdown */}
        <select
            id="searchMeasure"
            value={searchMeasure}
            onChange={(e) => setSearchMeasure(e.target.value)}
        >
          <option value="">All Measures</option>
          {uniqueMeasures.map((measure) => (
              <option key={measure} value={measure}>
                {measure}
              </option>
          ))}
        </select>
        {/* Metric Dropdown */}
        <select
            id="searchMetric"
            value={searchMetric}
            onChange={(e) => setSearchMetric(e.target.value)}
        >
          <option value="">All Metrics</option>
          {uniqueMetrics.map((metric) => (
              <option key={metric} value={metric}>
                {metric}
              </option>
          ))}
        </select>
        {/* Cause Name Dropdown */}
        <select
            id="searchCauseName"
            value={searchCauseName}
            onChange={(e) => setSearchCauseName(e.target.value)}
        >
          <option value="">All Cause Names</option>
          {uniqueCauseNames.map((causeName) => (
              <option key={causeName} value={causeName}>
                {causeName}
              </option>
          ))}
        </select>
      </div>


      {/* Rows Per Page Selection */}
      <div className="rows-per-page">
        <label htmlFor="rowsPerPage">Rows per page:</label>
        <select
            id="rowsPerPage"
            value={itemsPerPage === 'all' ? 'all' : itemsPerPage}
            onChange={(e) => {
              const value = e.target.value;
              setItemsPerPage(value === 'all' ? 'all' : Number(value));
              setCurrentPage(1); // Reset to first page when items per page changes
            }}
        >
          <option value={10}>10</option>
          <option value={20}>20</option>
          <option value={50}>50</option>
          <option value={100}>100</option>
          <option value="all">All</option>
        </select>
      </div>

      {/* Table Section */}
      <table className="data-table">
        <thead>
        </thead>
        <tbody>
          {currentItems.length === 0 ? (
            <tr>
              <td colSpan="100%">No data available.</td>
            </tr>
          ) : (
            currentItems.map((row, index) => (
              <tr key={row.index || index}>
                {Object.values(row).map((value, idx) => (
                  <td key={idx}>{value}</td>
                ))}
              </tr>
            ))
          )}
        </tbody>
      </table>

      {/* Pagination Component */}
      {filteredData.length > 0 && (
        <Pagination
          currentPage={currentPage}
          totalPages={totalPages}
          onPageChange={handlePageChange}
        />
      )}
    </div>
  );
};

export default Body;
