// src/components/Pagination.js
import React from 'react';
import './Pagination.css';

const Pagination = ({
  currentPage,
  totalPages,
  onPageChange,
  itemsPerPage,
  setItemsPerPage,
  filteredDataLength, // Added prop
}) => {
  // Generate an array of page numbers
  const pageNumbers = [];
  for (let i = 1; i <= totalPages; i++) {
    pageNumbers.push(i);
  }

  // Handle Previous Button Click
  const handlePrevious = () => {
    if (currentPage > 1) {
      onPageChange(currentPage - 1);
    }
  };

  // Handle Next Button Click
  const handleNext = () => {
    if (currentPage < totalPages) {
      onPageChange(currentPage + 1);
    }
  };

  // Handle Items Per Page Change
  const handleItemsPerPageChange = (e) => {
    const value = e.target.value;
    if (value === 'all') {
      setItemsPerPage(filteredDataLength); // Use the passed prop
    } else {
      setItemsPerPage(Number(value));
    }
    onPageChange(1); // Reset to first page when items per page changes
  };

  return (
    <div className="pagination-container">
      {/* Items Per Page Selection */}
      <div className="items-per-page">
        <label htmlFor="itemsPerPage">Rows per page:</label>
        <select
          id="itemsPerPage"
          value={itemsPerPage === filteredDataLength ? 'all' : itemsPerPage}
          onChange={handleItemsPerPageChange}
        >
          <option value={10}>10</option>
          <option value={20}>20</option>
          <option value={50}>50</option>
          <option value={100}>100</option>
          <option value="all">All</option>
        </select>
      </div>

      {/* Pagination Controls */}
      <div className="pagination-controls">
        <button
          onClick={handlePrevious}
          disabled={currentPage === 1}
          className="pagination-button"
        >
          &#8592; Previous
        </button>

        {/* Page Number Buttons */}
        {pageNumbers.map((number) => (
          <button
            key={number}
            onClick={() => onPageChange(number)}
            className={`pagination-button ${
              currentPage === number ? 'active' : ''
            }`}
          >
            {number}
          </button>
        ))}

        <button
          onClick={handleNext}
          disabled={currentPage === totalPages}
          className="pagination-button"
        >
          Next &#8594;
        </button>
      </div>
    </div>
  );
};

export default Pagination;
