// src/components/Body.js
import React, { useState, useEffect } from 'react';
import './Body.css';

const Body = () => {
  const [tableData, setTableData] = useState([]); // Original Data
  const [filteredData, setFilteredData] = useState([]); // Data after Filtering
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);

  // Search States
  const [searchCountry, setSearchCountry] = useState('');
  const [searchYear, setSearchYear] = useState('');
  const [searchPollutant, setSearchPollutant] = useState('');
  const [searchExposureMean, setSearchExposureMean] = useState('');
  const [searchUnits, setSearchUnits] = useState('');
  const [searchBurdenMean, setSearchBurdenMean] = useState('');
  const [searchMeasure, setSearchMeasure] = useState('');
  const [searchMetric, setSearchMetric] = useState('');
  const [searchCauseName, setSearchCauseName] = useState('');

  // Fetch data from FastAPI on component mount
  useEffect(() => {
    fetch('http://localhost:8000/api/data') // FastAPI endpoint
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        setTableData(data.data);      // Store original data
        setFilteredData(data.data);   // Initialize filtered data
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
        setError(true);
        setLoading(false);
      });
  }, []);

  // Handle Search Filtering
  useEffect(() => {
    let filtered = tableData;

    if (searchCountry.trim() !== '') {
      filtered = filtered.filter((item) =>
        item.Country.toLowerCase().includes(searchCountry.toLowerCase())
      );
    }

    if (searchYear.trim() !== '') {
      filtered = filtered.filter((item) =>
        item.Year.toString().includes(searchYear)
      );
    }

    if (searchPollutant.trim() !== '') {
      filtered = filtered.filter((item) =>
        item.Pollutant.toLowerCase().includes(searchPollutant.toLowerCase())
      );
    }

    if (searchExposureMean.trim() !== '') {
      filtered = filtered.filter((item) =>
        item['Exposure Mean'].toString().includes(searchExposureMean)
      );
    }

    if (searchUnits.trim() !== '') {
      filtered = filtered.filter((item) =>
        item.Units.toLowerCase().includes(searchUnits.toLowerCase())
      );
    }

    if (searchBurdenMean.trim() !== '') {
      filtered = filtered.filter((item) =>
        item['Burden Mean'].toString().includes(searchBurdenMean)
      );
    }

    if (searchMeasure.trim() !== '') {
      filtered = filtered.filter((item) =>
        item.Measure.toLowerCase().includes(searchMeasure.toLowerCase())
      );
    }

    if (searchMetric.trim() !== '') {
      filtered = filtered.filter((item) =>
        item.Metric.toLowerCase().includes(searchMetric.toLowerCase())
      );
    }

    if (searchCauseName.trim() !== '') {
      filtered = filtered.filter((item) =>
        item['Cause Name'].toLowerCase().includes(searchCauseName.toLowerCase())
      );
    }

    setFilteredData(filtered);
  }, [
    searchCountry,
    searchYear,
    searchPollutant,
    searchExposureMean,
    searchUnits,
    searchBurdenMean,
    searchMeasure,
    searchMetric,
    searchCauseName,
    tableData,
  ]);

  // Handle Reset Search
  const handleReset = () => {
    setSearchCountry('');
    setSearchYear('');
    setSearchPollutant('');
    setSearchExposureMean('');
    setSearchUnits('');
    setSearchBurdenMean('');
    setSearchMeasure('');
    setSearchMetric('');
    setSearchCauseName('');
  };

  if (loading) {
    return (
      <div className="body-container">
        <h2>Loading data...</h2>
      </div>
    );
  }

  if (error) {
    return (
      <div className="body-container">
        <h2>Error loading data.</h2>
      </div>
    );
  }

  return (
    <div className="body-container">
      <h2>Data from CSV</h2>

      {/* Search Section */}
      <div className="search-section">
        <div className="search-group">
          <input
            type="text"
            placeholder="Search by Country"
            value={searchCountry}
            onChange={(e) => setSearchCountry(e.target.value)}
          />
          <input
            type="text"
            placeholder="Search by Year"
            value={searchYear}
            onChange={(e) => setSearchYear(e.target.value)}
          />
          <input
            type="text"
            placeholder="Search by Pollutant"
            value={searchPollutant}
            onChange={(e) => setSearchPollutant(e.target.value)}
          />
        </div>
        <div className="search-group">
          <input
            type="text"
            placeholder="Search by Exposure Mean"
            value={searchExposureMean}
            onChange={(e) => setSearchExposureMean(e.target.value)}
          />
          <input
            type="text"
            placeholder="Search by Units"
            value={searchUnits}
            onChange={(e) => setSearchUnits(e.target.value)}
          />
          <input
            type="text"
            placeholder="Search by Burden Mean"
            value={searchBurdenMean}
            onChange={(e) => setSearchBurdenMean(e.target.value)}
          />
        </div>
        <div className="search-group">
          <input
            type="text"
            placeholder="Search by Measure"
            value={searchMeasure}
            onChange={(e) => setSearchMeasure(e.target.value)}
          />
          <input
            type="text"
            placeholder="Search by Metric"
            value={searchMetric}
            onChange={(e) => setSearchMetric(e.target.value)}
          />
          <input
            type="text"
            placeholder="Search by Cause Name"
            value={searchCauseName}
            onChange={(e) => setSearchCauseName(e.target.value)}
          />
        </div>
        <button onClick={handleReset} className="reset-button">Reset</button>
      </div>

      {/* Table Section */}
      <table className="data-table">
        <thead>
          <tr>
            {/* Dynamically create table headers based on keys */}
            {filteredData.length > 0 &&
              Object.keys(filteredData[0]).map((key) => (
                <th key={key}>{key.toUpperCase()}</th>
              ))}
          </tr>
        </thead>
        <tbody>
          {filteredData.length === 0 ? (
            <tr>
              <td colSpan="100%">No data available.</td>
            </tr>
          ) : (
            filteredData.map((row, index) => (
              <tr key={index}>
                {Object.values(row).map((value, idx) => (
                  <td key={idx}>{value}</td>
                ))}
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Body;
